import React from 'react';

class Todos extends React.Component{

    state = {
        todos: ["break fast", "morning exercise", "readying", "studying"],
        del: [],

    }

    action = (index) => {
      const arr = [...this.state.todos];
      const delarr = [...this.state.del];
        /*save deleted item on del state*/
      delarr.push(arr[index]);
      this.setState({
          ...this.state,
          del: delarr
      })
      /*update todos state*/
      arr.splice(index,1);
      this.setState({todos: arr})

      console.log(this.state.del)
    }


    addtodo = (e) =>{
        e.preventDefault()
        let newarr = [...this.state.todos];
        newarr.push(e.target.elements.option.value)
        console.log(e.target.elements.option.value)
        this.setState({todos: newarr})
        e.target.elements.option.value = ""
    }

    reset = ()=>{
        this.setState({todos: []})
    }

    randomTodo = () =>{
        const random = Math.floor(Math.random()*this.state.todos.length) 
        alert(random)
    }

    render(){
        return(
            <div>
                <button onClick={this.reset}>restet</button>
                <button onClick={this.randomTodo}>what should i do today?</button>
                <ul>

                {this.state.todos.map((todo,index)=>
                    <li key={index}>
                    {todo}
                    <button onClick={()=>this.action(index)}>Done</button>
                    </li>
                )

              }
              </ul>
            <form onSubmit={this.addtodo}>
                <input type="text" placeholder="add new task" name="option"/>
                <button >add</button>
            </form>

            </div>
        );
    }
}

export default Todos;
