import React from 'react';

class Todos extends React.Component{

    state = {
        todos: [
            {id:1 , todo: "study React"},
            {id: 2, todo:"study Redux"},
            {id: 3, todo: "study nodeJS"}

        ],
        del: []
    }

    action = (index) => {
      const arr = [...this.state.todos];
      const delarr = [...this.state.del];
        /*save deleted item on del state*/
      delarr.push(arr[index]);
      this.setState({
          ...this.state,
          del: delarr
      })
      /*update todos state*/
      arr.splice(index,1);
      this.setState({todos: arr})

      console.log(this.state.del)
    }

    getinputs = (e) =>{
        let inputVal = e.target.value;
        console.log(inputVal);
       this.setState({todos:{
           ...this.state.todos

       }})
    }


    render(){
        return(
            <div>
                <ul>
                {this.state.todos.map((todo,index)=>
                    <li key={todo.id}>
                    {todo.id}{todo.todo}
                    <button onClick={()=>this.action(index)}>complete</button>

                    </li>
                )

              }
              </ul>

             <input type="text" placeholder="add new task" onChange={this.getinputs}/>
             <button onClick>add</button>

            </div>
        );
    }
}

export default Todos;
