import React from 'react';

class Todos extends React.Component{

    state = {
        todos: [
            {id:1 , todo: "study React"},
            {id: 2, todo:"study Redux"},
            {id: 3, todo: "study nodeJS"}

        ],
        del: []
    }

    action = (index) => {
      const arr = [...this.state.todos];
      const delarr = [];

      delarr.push(arr[index]);
      this.setState({
          ...this.state.todos,
          del: delarr
      })
      arr.splice(index,1);
      this.setState({todos: arr})



    }
    render(){
        return(
            <div>
                {this.state.todos.map((todo,index)=>
                    <li key={todo.id}>
                    {todo.todo}
                    <button onClick={()=>this.action(index)}>complete</button>

                    </li>
                )}
            </div>
        );
    }
}

export default Todos;
